import React, {useEffect} from 'react'
import {v4 as uuidv4} from "uuid"
import axios from 'axios';
const Form = ({input, setInput, todos, setTodos,editTodo ,setEditToDo,userName, setUserName}) => {
  const userNameTextHandler = (e) =>{
    setUserName(e.target.value);
  }
 
  const updateTodo = (title,id,completed) => {
      const newTodo = todos.map((todo) => 
        todo.id === id ? {title,id,completed} : todo
      )
      setTodos(newTodo);
      setEditToDo("");
  }
  useEffect(() => {
    if(editTodo){
      setInput(editTodo.title);
    } else{
      setInput("");
    }
  }, [setInput,editTodo])
  const onInputChange = (e) =>{
    setInput(e.target.value);
   };

  const onFormSubmit = (e) =>{
    e.preventDefault();
    if(!editTodo){
      setTodos([...todos, {id: uuidv4(), title:input, completed:false}])
      
        axios.post("https://630fada536e6a2a04edfbc08.mockapi.io/todos",{
          todovalue: title,
          todoid: id,
        }).then((response) => {setapiInput(response.data)})
        
      
      setInput("");
    } else{
      updateTodo(input, editTodo.id, editTodo.completed)
    }
   
   };
       //Set Api
       async function createPost(todo) {
        await axios.post("https://630fada536e6a2a04edfbc08.mockapi.io/todos",{
          
        }).then((response) => {setapiInput(response.data)})
        
      }
    
      //Get Api 
      axios.get("https://630fada536e6a2a04edfbc08.mockapi.io/todos").then((res) => {
        const lngt = res.data.length;
        
        //setapiInput(res.data.length)
      }, [])
    
      //Update Api
      async function updatePost() {
        await axios.put("https://630fada536e6a2a04edfbc08.mockapi.io/todos/"+1,{
          todovalue: "Deneme",
          todoid: 1
        }).then((response) => {setapiInput(response.data)})
      }
    
      //Delete Api
      async function deletePost() {
        await axios.delete("https://630fada536e6a2a04edfbc08.mockapi.io/todos/"+1,{
          
        }).then(() => {alert("Todo delete!"); setapiInput(null)})
      }

  return (
    <form onSubmit={onFormSubmit}>
        <input 
            type="text" 
            placeholder = "Enter a To Do..." 
            className = "task-input" 
            value={input}
            required
            minLength={3}
            onChange={onInputChange}
        />
           <input 
            type="text" 
            placeholder = "Enter Name..." 
            className = "user-input" 
            value={userName}
            required
            onChange={userNameTextHandler}
        />
        <button className='button-add' type='submit'>
            {editTodo ? "OK" : "Add"}
        </button>

    </form>
  );
};

export default Form
